{
	setMotorSpeed(rightMotor, 0);
	setMotorSpeed(leftMotor, 50);
	sleep(1000);
}

//gerak mengikuti garis hitam
void maju()
{
	float temp = (float) getColorReflected(colorSensor) * 2 / 5 + 20;
	motor[leftMotor]  = temp;
	motor[rightMotor] = 80 - temp;
}

//gerakan oriental********************************************************
